mapper.select:
select
*
from t_user_contact
where user_id = #{userId}
and contact_user_id = #{contactUserId}

    and status = #{status}

mapper.update:
delete from t_user_contact
where user_id = #{userId}
and contact_user_id = #{contactUserId}

update t_user_rela
set
rela_is_starred = #{isStarred}
where rela_user_id = #{userId}
and rela_contact_id = #{contactId}

update t_user_rela
set
rela_is_blocked = #{isBlocked}
where rela_user_id = #{userId}
and rela_contact_id = #{contactId}
