mapper.select:
select
*
from t_user_rela
where rela_user_id = #{userId}
and rela_friend_id = #{friendId}

    and rela_status = #{status}

mapper.update:
update t_user_rela set
rela_status = #{relaStatus},
rela_friend_phone = #{relaFriendPhone}, rela_friend_remark = #{relaFriendRemark}, rela_friend_desc = #{relaFriendDesc}, rela_update_time = now() where rela_id = #{relaId}

delete from t_user_rela
  where rela_user_id = #{userId}
    and rela_friend_id = #{friendId}

update t_user_rela
set
rela_is_star_friend = #{isStarFriend}
where rela_user_id = #{userId}
  and rela_friend_id = #{friendId}
